        #region ListAppLinks Tests

        [Fact]
        public async Task ListAppLinks_ReturnsAppLinksList()
        {
            // Arrange
            var userId = "user-app-links-123";

            var responseJson = @"[
                {
                    ""id"": ""app-link-1"",
                    ""label"": ""Sample App 1"",
                    ""linkUrl"": ""https://test.okta.com/home/app1/instance1"",
                    ""logoUrl"": ""https://test.okta.com/assets/img/logos/app1.png"",
                    ""appName"": ""sample1"",
                    ""appInstanceId"": ""instance1"",
                    ""appAssignmentId"": ""assignment1"",
                    ""credentialsSetup"": false,
                    ""hidden"": false,
                    ""sortOrder"": 1
                },
                {
                    ""id"": ""app-link-2"",
                    ""label"": ""Sample App 2"",
                    ""linkUrl"": ""https://test.okta.com/home/app2/instance2"",
                    ""logoUrl"": ""https://test.okta.com/assets/img/logos/app2.png"",
                    ""appName"": ""sample2"",
                    ""appInstanceId"": ""instance2"",
                    ""appAssignmentId"": ""assignment2"",
                    ""credentialsSetup"": true,
                    ""hidden"": false,
                    ""sortOrder"": 2
                }
            ]";

            var mockClient = new MockAsyncClient(responseJson);
            var userResourcesApi = new UserResourcesApi(mockClient, new Configuration { BasePath = "https://test.okta.com" });

            // Act
            var appLinks = await userResourcesApi.ListAppLinksWithHttpInfoAsync(userId);

            // Assert
            appLinks.Should().NotBeNull();
            appLinks.Data.Should().HaveCount(2);
            appLinks.Data[0].Label.Should().Be("Sample App 1");
            appLinks.Data[0].AppName.Should().Be("sample1");
            appLinks.Data[0].CredentialsSetup.Should().BeFalse();
            appLinks.Data[1].Label.Should().Be("Sample App 2");
            appLinks.Data[1].CredentialsSetup.Should().BeTrue();

            mockClient.ReceivedPath.Should().StartWith("/api/v1/users/{id}/appLinks");
            mockClient.ReceivedPathParams.Should().ContainKey("id");
            mockClient.ReceivedPathParams["id"].Should().Contain(userId);
        }

